  static late SharedPreferences prefs;
  static late int level;
  static late String modeView;
  static late int indexWordView;
  static List<String> lsMode = ["readRu_writeEn", "readEn_writeRu", "hearRu_writeEn", "readEn_speakRu"];
  static int nbMinWord = 20;
  static int minWordKnowlege = 5;

  static int getProba(List<int> list) {
    // reverse list
    int minValue = list.reduce(min);
    for (int i = 0; i < list.length; i++) {
      list[i] -= minValue;
    }
    int maxValue = list.reduce(max);
    for (int i = 0; i < list.length; i++) {
      list[i] = maxValue - list[i] + 1;
    }

    // select lucky index
    int sum = list.reduce((value, element) => value + element);
    int nba = Random().nextInt(sum);
    int luckyIndex = 0;
    int sumIndexPassed = 0;
    while (luckyIndex < list.length) {
      if (nba < list[luckyIndex] + sumIndexPassed) break;
      sumIndexPassed += list[luckyIndex];
      luckyIndex++;
    }

    return luckyIndex;
  }

  static void getQuestion() {
    // get index word know
    List<int> lsProbaWord = [];
    int i = 0;
    while (i < lsWord.length) {
      int nbWordKnowlege = 0;

      bool isBreaked = false;
      for (String mode in lsMode) {
        int wordKnowlege = lsWord[i][mode] as int;
        nbWordKnowlege += wordKnowlege;
        if (wordKnowlege < minWordKnowlege && i >= nbMinWord) isBreaked = true;
      }
      if (isBreaked) break;

      lsProbaWord.add(nbWordKnowlege);
      i++;
    }

    // update level
    level = lsProbaWord.length - nbMinWord + 1;

    indexWordView = getProba(lsProbaWord);
    List<int> lsProbaMode = [];
    for (String mode in lsMode) {
      int modeKnowlege = lsWord[indexWordView][mode] as int;
      lsProbaMode.add(modeKnowlege);
    }
    modeView = lsMode[getProba(lsProbaMode)];
  }

  static getCurrentWord() {
    return lsWord[indexWordView];
  }
  static void endQuestion(int nb) {
    // update note
    int note = lsWord[indexWordView][modeView] as int;
    lsWord[indexWordView][modeView] = note + nb;

    // save data
    prefs.setString("lsWord", jsonEncode(lsWord));

    getQuestion();
  }
  
  static void init() async {
    // select words
    prefs = await SharedPreferences.getInstance();
    String? savedLsWord = prefs.getString("lsWord");
    if (savedLsWord != null) {
      lsWord = jsonDecode(savedLsWord);
    }
    getQuestion();

    print(getProba([0, 0, 0]));
  }
